import React from 'react';

function CatApp() {
    const [statusCode, setStatusCode] = React.useState(200);
    const [items, setItems] = React.useState(null);
    const [err, setErr] = React.useState(null);
    const isValidStatusCode = (code) => /^[1-5][0-9]{2}$/.test(code);


    React.useEffect(() => {
        if (!isValidStatusCode(statusCode)) {
            setErr('Invalid Status Code !!');
            setItems(null);
            return;
          }
        fetch(`${statusCode}`)
            .then((res) => {
                if (!res.ok){
                    throw new Error('Network response failure');
                }
                return res.blob();
            })
            .then((blob) => {
                const imgUrl = URL.createObjectURL(blob);
                setItems(imgUrl);

            })
            .catch((error) => {
                setErr('Fetch data failure');
                setItems(null);
                console.error('Error fetching item:', error)
            });

    }, [statusCode] );
    const inputChange = (e) => {
        const val = e.target.value;
        setStatusCode(val);
    };
    return (
        <div>
            <h2>Check Your Status</h2>
            <div>
                <label htmlFor='statusInput'> Enter HTTP Status Code: </label>
                <input type='number' id='statusInput' value={statusCode}
                 onChange={inputChange}/>

            </div>
            <div>
                {err && <p>Error: {err}</p>}
                {items && <img src={items} alt={`Cat status code depiction for ${statusCode}`} />}
            </div>
        </div>
    );
}
export default CatApp;
