import React from "react";

const CatApp = () => {
  const [statusCode, setStatusCode] = React.useState("");
  const [items, setItems] = React.useState(null);
  const [isDataLoaded, setIsDataLoaded] = React.useState(false);

  const fetchCatImage = () => {
    if (!statusCode || isNaN(statusCode)) {
      alert("Please enter a valid status code!");
      return;
    }

    setIsDataLoaded(false);
    const imageUrl = `https://http.cat/${statusCode}`;
    setItems(imageUrl);
    setIsDataLoaded(true);
  };

  return (
    <div className="App">
      <h1>HTTP Cat Images</h1>
      <input
        type="text"
        placeholder="Enter status code"
        value={statusCode}
        onChange={(e) => setStatusCode(e.target.value)}
      />
      <button onClick={fetchCatImage}>Get Cat Image</button>

      {isDataLoaded && items && (
        <div>
          <img src={items} alt={`HTTP Cat for status ${statusCode}`} />
          <p>Status Code: {statusCode}</p>
        </div>
      )}

      {isDataLoaded && !items && (
        <div>
          <p>No cat image found for status code: {statusCode}</p>
        </div>
      )}
    </div>
  );
};

export default CatApp;
