import React, { useState, useEffect } from 'react';

function CatApp() {
  const [statusCode, setStatusCode] = useState(200);
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    if (!Number.isInteger(statusCode) || statusCode < 100 || statusCode > 599) {
      // Handle invalid status code
      return;
    }

    fetch(`/${statusCode}`) // Assuming the proxy is properly set in package.json
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response failure');
        }
        return response.url; // Assuming the response provides the image URL directly
      })
      .then(url => {
        setImageUrl(url); // Set the received URL for the image
      })
      .catch(error => {
        // Handle errors
        console.error('Error fetching image:', error);
      });
  }, [statusCode]);

  const inputChange = (e) => {
    const val = e.target.value;
    setStatusCode(val);
  };

  return (
    <div>
      <h2>Check Your Status</h2>
      <div>
        <label htmlFor='statusInput'>Enter HTTP Status Code: </label>
        <input type='number' id='statusInput' value={statusCode} onChange={inputChange} />
      </div>
      <div>
        {imageUrl && <img src={imageUrl} alt={`Cat status code depiction for ${statusCode}`} />}
      </div>
    </div>
  );
}

export default CatApp;
