import React, { useState, useEffect } from "react";

const App = () => {
  const [items, setItems] = useState([]);
  const [isDataLoaded, setIsDataLoaded] = useState(false);

  useEffect(() => {
    fetch("https://http.cat/")
      .then((res) => res.json())
      .then((json) => {
        setItems([json]);
        setIsDataLoaded(true);
      })
      .catch((error) => {
        console.error("Error fetching data: ", error);
        setIsDataLoaded(true); // Set data loaded to true even on error
      });
  }, []);

  if (!isDataLoaded) {
    return (
      <div>
        <h1>Please wait, data is still loading</h1>
      </div>
    );
  }

  return (
    <div className="App">
      <h1>HTTP Cat Images</h1>
      {items.map((item) => (
        <div key={item.url}>
          <img src={item.url} alt="HTTP Cat" />
          <p>Status Code: {item.status}</p>
        </div>
      ))}
    </div>
  );
};

export default App;
