import React, { useState, useEffect } from 'react';

function CatApp() {
  const [statusCode, setStatusCode] = useState(200);
  const [imageUrl, setImageUrl] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!Number.isInteger(statusCode) || statusCode < 100 || statusCode > 599) {
      setError('Wrong Status Code !!');
      setImageUrl('');
      return;
    }

    fetch(`/${statusCode}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response failure');
        }
        return response.blob();
      })
      .then(blob => {
        const url = URL.createObjectURL(blob);
        setImageUrl(url);
        setError(null);
      })
      .catch(error => {
        setError('Fetch data failure');
        setImageUrl('');
        console.error('Error fetching image:', error);
      });

  }, [statusCode]);

  const inputChange = (e) => {
    const val = e.target.value;
    setStatusCode(val);
  };

  return (
    <div>
      <h2>Check Your Status</h2>
      <div>
        <label htmlFor='statusInput'>Enter HTTP Status Code: </label>
        <input type='number' id='statusInput' value={statusCode} onChange={inputChange} />
      </div>
      <div>
        {error && <p>Error: {error}</p>}

        <img src="https://http.cat/404" alt={`Cat status code depiction for 404`} />
      </div>
    </div>
  );
}

export default CatApp;
